Rules.InvariantScript:
ClearCollect(col_Orders, ForAll(Sequence(50), { ID: Value, Order: "Order-" & Value }));
    ClearCollect(col_Messages, ForAll(Sequence(50), { ID: Value, Msg: "Msg-" & Value }));
    ClearCollect(col_Profile, ForAll(Sequence(50), { ID: Value, Name: "User-" & Value, Role: "Ninja" }));
    ClearCollect(col_Tasks, {ID:1, Task:"GetUserProfile"}, {ID:2, Task:"FetchOrders"}, {ID:3, Task:"RetrieveNotifications"});
    Clear(col_Results);
    Clear(col_ConcurrentResults);
    UpdateContext({ lvar_timerstart: false});
    UpdateContext({ lvar_Status: "Initialized 50 records per collection"})

Concurrent(
        ClearCollect(col_OrdersResult, col_Orders),
        ClearCollect(col_MessagesResult, col_Messages),
        ClearCollect(col_ProfileResult, col_Profile)
    );
    ClearCollect(
        col_ConcurrentResults,
        col_OrdersResult,
        col_MessagesResult,
        col_ProfileResult
    );
    UpdateContext({ lvar_Status: "Concurrent clones finished: " & CountRows(col_ConcurrentResults) & " records" });

